prop_vax_infected <- function(prop_vax, vax_efficiency = .95){
	prop_unvax <- 1- prop_vax
	
	prop_vax*(1-vax_efficiency)/(prop_vax*(1-vax_efficiency) + prop_unvax )                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        )

}
x_range <- seq(0,1,.01)
par(pty="s")
par(mar=c(3,0,3,0), mgp=c(1.7, .5, 0), tck=-.01)
plot(NA, xlim=c(0,1), ylim = c(0,1), 
		 ylab = "Proportion Vaccinated (Cases)",
		 xlab = "Proportion Vaccinated (Population)")
lines(x_range, prop_vax_infected(x_range), lty = 2)
lines(x_range, prop_vax_infected(x_range, vax_efficiency = .8), lty = 3)
lines(x_range, prop_vax_infected(x_range, vax_efficiency = .75), lty = 4)
text(x = .6, y = prop_vax_infected(.6), 
		 label = "95% Ve", cex = .8)
text(x = .7, y = prop_vax_infected(.7, vax_efficiency = .80), 
		 label = "80% Ve", cex = .8)
text(x = .8, y = prop_vax_infected(.8, .75), 
		 label = "75% Ve", angle = 45, cex = .8)

